import React, {useEffect, useCallback} from 'react'

export const ChatWindow = ( {getAllMessages, getOnlineUsers, loading} )=> {

  const {allMessages} = useCallback( () => {getAllMessages()} )

  // useEffect(() => {
  //   getAllMessages()
  // }, [getAllMessages])

  if (loading) {
    return (<div>'ololo'</div>)
  }


  if (!allMessages) {
    return (
      <div> No any messages yet </div> )
  }

  return (
        <div>
          { allMessages.map( (message, index) => {
            return (
              <div>
                {message.text}
              </div>
            )
          } ) }
        </div>
  )
}
