import React, {useContext} from 'react'
import {useState, useEffect, useCallback} from 'react'
import {ChatWindow} from '../components/OneChatMessageComp' // ???
import {AuthContext} from '../context/AuthContext'




export const ChatWorker =({ chat, token, chatId, socket, allMessages}) => {

  const auth = useContext(AuthContext)
  const userId = auth.userId

  // Indicator for updating component
  const [listMessages, setListMessages] = useState([])


  var sendHandler = (event) => {
    event.preventDefault()
    const newMessageFromClient = event.target.elements.newMessageInput.value
    event.target.elements.newMessageInput.value = null
    socket.emit('New message from client', newMessageFromClient, chatId, userId)
  }


  // Initialize socket listeners and emit when user connected
  const createSocket = useCallback( () => {

    socket.on('New user message from server', (newMessage) => {
      console.log( allMessages(newMessage) )
      setListMessages(newMessage)
      // setMessageToShow( [...messageToShow, newMessage] )
    })

    socket.on('Refresh online users', (onlineUsers) => {
      console.log('Сейчас онлайн: ', onlineUsers)
    })

    socket.on('Old messages', (oldMessages) => {
      console.log( allMessages(oldMessages) )
      setListMessages(oldMessages)
      // setMessageToShow( oldMessages )
    })

    // Make emit to server
    socket.emit('Client just enter', chatId, userId)

  /* eslint-disable-next-line */
  }, [chatId, userId] )


  // Run when page will loading
  useEffect(() => {
    createSocket()
  }, [createSocket])





  return (
  <div style={{marginTop: '2rem', width: '100%'}} >

    <link href="https://fonts.googleapis.com/icon?family=Material+Icons" rel="stylesheet"></link>
    {/* eslint-disable-next-line */}
    <a hreaf="/" className="waves-effect waves-light btn"><i className="material-icons right"> near_me </i> Chat </a>
    {/* eslint-disable-next-line */}
    <a href="#" className="waves-effect waves-light btn"><i className="material-icons right">live_tv</i> *Video Call* </a>

    <div className="row">
      <div className="col" style={{marginLeft: '-0.75rem', width:'100%'}}>
        <div className="card blue lighten-5">



        <div className="card-content black-text" >
          <span className="card-title pink-text"> Chat: {chat.chatname} </span>
          <span className="card-title pink-text"> Online: {chat.chatname} </span>
          {/* <ChatWindow messageToShow={messageToShow}/>      */}




          <div id="chat-zone">
            { listMessages.map( (message, index) => {

              // Working with datetime
              var dt = message.date
              var s = dt.split('-').join(',').split('T').join(',').split(':').join(',').split('.').join(',').split(',')
              var cur = new Date(s[0], s[1]-1, s[2], s[3], s[4], s[5])
              var off = cur.getTimezoneOffset();
              cur.setMinutes(cur.getMinutes() - off )
              var datetime = cur.toLocaleString('ru-RU', { timeZone: 'Europe/Moscow' }) // it's dont work. set default time as in browser

              return (
                <div key={message._id} style={{marginTop: '1rem'}}>

                  <span className="msg-author">
                    {message.login}{'\u00A0'}
                  </span>

                  <span className="msg-date">
                    { datetime }
                  </span>

                  <span className="msg-text flow-text">
                    {message.text}
                  </span>
                </div>
              )
            } ) }
          </div>


        </div>

          <div className="card-action">

            <form id="chat-form" onSubmit={ sendHandler }>

              <div className="input-field col s12">
                <input 
                  id="newMessageInput"
                  type="text"
                  maxLength="50"
                  name="newMessageInput"
                  className="materialize-textarea"
                  placeholder="Your message"
                  autoComplete="off"
                  />
              </div>

              <button 
                className="btn pink lighten-3" 
                style={{ marginLeft: '0.75rem'}}>
                Send
              </button>

            </form>

          </div>

        </div>
      </div>
    </div>
  </div>
  )
}
