import React, {useCallback, useContext, useEffect, useState} from 'react'
import {useParams} from 'react-router-dom'
import {useHttp} from '../hooks/http.hook'
import {AuthContext} from '../context/AuthContext'
import {Loader} from '../components/LoaderComp'
import {ChatWorker} from '../components/ChatWorkerComp'


export const ChatroomsPage = () => {

  console.log('init')

  const {token} = useContext(AuthContext)
  const {request, loading} = useHttp()
  const [chatrooms, setChatrooms] = useState(null)


  const getChatrooms = useCallback(async () => {
    console.log('inside')
    try {
      const fetched = await request (
        `/api/chat/chatrooms`,
        'GET',
        null,
        {Authorization: `Bearer ${token}`}
      )
      setChatrooms(fetched) 
      console.log(fetched)
      chatrooms = fetched
    } catch (e) {}
  }, [token, request])

  useEffect(() => {
    getChatrooms()
  }, [])

    // If still loading data from backend
    if (loading) {
      return <Loader />
    }

  return (
    <div>
      <h1 className="blue-text">All Chatrooms</h1>
      <div className="collection">
        { !loading &&chatrooms && chatrooms.map( (chat, index) => {
          return (
            <a href="#!" key={index} className="collection-item">Alvin</a>
          )
        } ) }
