import React, {useContext} from 'react'
import {useState, useEffect, useCallback} from 'react'
import {ChatWindow} from '../components/OneChatMessageComp' // ???
import {AuthContext} from '../context/AuthContext'


export const ChatWorker =({ chat, token, chatId, socket}) => {

  console.log(chat.onlineUsers)

  const auth = useContext(AuthContext)
  const userId = auth.userId


  var loading = true
  // work lists!!!!!!!!!
  // const [loading, setLoading] = useState(false)
  // const incr = useCallback( () => {
  //   setLoading( l => !l )
  // }, [setLoading])

  // useEffect(() => {
  //   incr()
  // }, [incr])

  const [allMessages, setAllMessages] = useState([])

  const updateMessages = useCallback( (newMessage) => {
    const _ = [newMessage].concat(allMessages)
    setAllMessages( _ )
    console.log(allMessages)
  }, [])

  useEffect(() => {
    updateMessages(newMessage)
  }, [[updateMessages, newMessage]])

  var newMessage

  setTimeout( () => { newMessage = {text: 'afasfas'}}, 5000 )
  setTimeout( () => { newMessage = {text: 'afasfas'}}, 5000 )
  setTimeout( () => { newMessage = {text: 'afasfas'}}, 5000 )


  // ????? ______if add this lines too much sockets open_____
  // const [allMessages, setMessages] = useState([])
  // const getFresh = useCallback ( (newMessage) => {setMessages([newMessage].concat(allMessages))}, [setMessages, allMessages] )

  socket.on('New user connected to chatroom', (user, login) => { console.log('Пришел: ', user, login) })

  socket.on('New user message from server', (newMessage) => {
    // ????? ______if add this lines too much sockets open_____
    // getFresh(newMessage, messages)
    const _ = [newMessage].concat(allMessages)
    allMessages = _
    console.log(allMessages)
  })

  socket.on('User left chatroom', (user, login) => console.log('Ушел: ', user, login))


  // Make emit for server to get old message in this chat. Second parametr count=50 is optional
  // Backend server will turn this user in onilne status
  socket.on('Old messages', (oldMessages) => {
    allMessages = oldMessages
  })
  socket.emit('Client just enter', chatId, userId)



  const sendHandler = (event) => {
    event.preventDefault()
    const newMessageFromClient = event.target.elements.newMessageInput.value
    event.target.elements.newMessageInput.value = null

    // Emit this message to server socket
    socket.emit('New message from client', newMessageFromClient, chatId, userId)
  }

  if (loading) {
    return (<div>'ololo'</div>)
  } if (!loading) {
    return (<div>'AAAAAAAAAAAAA'</div>)
  }

  return (
  <div style={{marginTop: '2rem', width: '100%'}} >

    <link href="https://fonts.googleapis.com/icon?family=Material+Icons" rel="stylesheet"></link>
    {/* eslint-disable-next-line */}
    <a hreaf="/" className="waves-effect waves-light btn"><i className="material-icons right"> near_me </i> Chat </a>
    {/* eslint-disable-next-line */}
    <a href="#" className="waves-effect waves-light btn"><i className="material-icons right">insert_emoticon</i> *Members* </a>
    {/* eslint-disable-next-line */}
    <a href="#" className="waves-effect waves-light btn"><i className="material-icons right">live_tv</i> *Video Call* </a>

    <div className="row">
      <div className="col" style={{marginLeft: '-0.75rem', width:'100%'}}>
        <div className="card blue lighten-5">




        <div className="card-content black-text" >
          <span className="card-title">Chat Name</span>


          <ChatWindow loading={loading}/>

        </div>








          <div className="card-action">

            <form id="chat-form" onSubmit={ sendHandler }>

              <div className="input-field col s12">
                <textarea 
                  id="newMessageInput"
                  name="newMessageInput"
                  className="materialize-textarea"
                  />
                <label htmlFor="textarea">Your Message</label>
              </div>

              <button 
                className="btn pink lighten-3" 
                style={{ marginLeft: '0.75rem'}}>
                Send
              </button>

            </form>

          </div>

        </div>
      </div>
    </div>
  </div>
  )
}
