import React, {useState, useEffect} from 'react'

import {useHttp} from '../hooks/http.hook'
import {useMessage} from '../hooks/message.hook'


// Common resourses for both components
function useFormHandler ( formInputValues, uri ) {

  // Import myself created hooks and other hooks
  const {loading, error, request, clearError} = useHttp()
  const message = useMessage()
  const [form, setForm] = useState( formInputValues )

  useEffect( () => {
    message(error)
    clearError()
  }, [error, message, clearError])

  // Refreshing form inputs value onChange
  const changeHandler = event => {
    setForm({...form, [event.target.name]: event.target.value })
  }

  // Have unique URI for different components
  const submitHandler = async () => {
    try {
      const data = await request(uri, 'POST', {...form})
      message(data.message)
    } catch (e) {} // errors already processed in useHttp hook
  }

  return {form, loading, message, request, changeHandler, submitHandler}
}



// COMPONENT for Registration Form
function AuthPageRegister() {

  // Init resourses: hooks, forms, handlers
  const uri = '/register'
  const {form, loading, message, request, changeHandler, submitHandler} = useFormHandler({email: '', login:'', password: ''}, uri);

return (
  <div className="input-container">

    <div className="input-field">
      <input 
        id="email" 
        name="email"
        type="text" 
        className="auth" 
        onChange={changeHandler} 
        />
      <label htmlFor="email">E-mail</label>
    </div>

    <div className="input-field">
      <input 
        id="login" 
        name="login"
        type="text" 
        className="auth" 
        onChange={changeHandler} 
        />
      <label htmlFor="login">Login</label>
    </div>

    <div className="input-field">
      <input 
        id="password" 
        name="password"
        type="password" 
        className="auth" 
        onChange={changeHandler} />
      <label htmlFor="password">Password</label>
    </div>

  <button 
    className="btn blue lighten-2" 
    onClick={submitHandler}
    disabled={loading} >
    Sign Up!
  </button>

</div>
)}


// COMPONENT for Login Form
function AuthPageLogin() {

  // Init resourses: hooks, forms
  uri='/login'
  const {form, loading, message, request, changeHandler, submitHandler} = useFormHandler({login:'', password: ''});


return (
  <div className="input-container">

      <div className="input-field">
        <input 
          id="login" 
          name="login"
          type="text" 
          className="auth" 
          onChange={changeHandler} 
          />
        <label htmlFor="login">Login</label>
      </div>

      <div className="input-field">
        <input 
          id="password" 
          name="password"
          type="password" 
          className="auth" 
          onChange={changeHandler} />
        <label htmlFor="password">Password</label>
      </div>

      <button 
        className="btn blue lighten-2" 
        onClick={submitHandler}
        disabled={loading} >
        Login!
      </button>

  </div>
  )}

  export {AuthPageLogin, AuthPageRegister}
