import {createContext} from 'react'

import openSocket from 'socket.io-client'
const socket = openSocket()

function noop() {}

export const AuthContext = createContext({
  token: null,
  userId: null,
  login: noop,
  logout: noop,
  isAuthorized: false,
  socket: socket,

})
