import React from 'react'
import {Switch, Route, Redirect} from 'react-router-dom'
import {AuthPage} from './pages/AuthPage'
import {ChatroomsPage} from './pages/ChatroomsPage'
import {CreateChatPage} from './pages/CreateChatPage'
import {OneChatPage} from './pages/OneChatPage'

import {AuthPageLogin} from './pages/AuthPage-Login'
import { AuthPageRegister } from './pages/AuthPage-Register'

export const useRoutes = (isAuthorized) => {
  if (isAuthorized) {
    return (
      <Switch>
        <Route path="/chatrooms" exact>
          <ChatroomsPage />
        </Route>
        <Route path="/create" exact>
          <CreateChatPage />
        </Route>
        <Route path="/chat/:id">
          <OneChatPage />
        </Route>
        <Redirect to="/chatrooms" />
      </Switch>
    )
  } else {
    return (
      <Switch>
        <Route path="/" exact>
          <AuthPage />
        </Route>

        {/* <Route path="/login" component={OneChatPage} /> */}

        <Redirect to="/" />
      </Switch>
    )
  }
}
