import React, {useEffect, useCallback} from 'react'

export const ChatWindow = ( {getAllMessages, getOnlineUsers} )=> {

  const {allMessages} = useCallback( () => {getAllMessages()} )

  useEffect(() => {
    getAllMessages()
  }, [getAllMessages])

  if (!allMessages) {
    return (
      <div> No any messages yet </div> )
  }

  return (
        <div>
          { allMessages.map( (message, index) => {
            return (
              <div>
                {message.text}
              </div>
            )
          } ) }
        </div>
  )
}
