import React, {useState} from 'react'
import {useHttp} from '../hooks/http.hook'
import {ChatWindow} from '../components/ChatWindowComp'

export const ChatWorker = ({ chat, token, chatId}) => {

  console.log(chat)

  const [newMessage, setNewMessage] = useState(null)
  const {request, loading} = useHttp()

  const changeHandler = event => {
    setNewMessage(event.target.value)
  }

  // make request with newMessage
  const sendHandler = async () => {
    try {
      const fetched = await request (
        `/api/chatworker/chatrooms/${chatId}/send_new_message`,
        'POST',
        {text: newMessage},
        {Authorization: `Bearer ${token}`}
      )
      // ???????????
      console.log(fetched) 
    } catch (e) {}
  }



  return (

  // Connect socket.io for client
  <div style={{marginTop: '2rem', width: '100%'}} >

    <script src="/socket.io/socket.io.js"></script>
    <script>var socket = io();</script>
    <link href="https://fonts.googleapis.com/icon?family=Material+Icons" rel="stylesheet"></link>

    {/* eslint-disable-next-line */}
    <a hreaf="/" className="waves-effect waves-light btn"><i className="material-icons right"> near_me </i> Chat </a>
    {/* eslint-disable-next-line */}
    <a href="#" className="waves-effect waves-light btn"><i className="material-icons right">insert_emoticon</i> *Members* </a>
    {/* eslint-disable-next-line */}
    <a href="#" className="waves-effect waves-light btn"><i className="material-icons right">live_tv</i> *Video Call* </a>

    <div className="row">
      <div className="col" style={{marginLeft: '-0.75rem', width:'100%'}}>
        <div className="card blue lighten-5">

        <ChatWindow />

          <div className="card-action">

              <div className="input-field col s12">
                <textarea 
                  id="textarea" 
                  className="materialize-textarea"
                  onChange={changeHandler} />
                <label htmlFor="textarea">Your Message</label>
              </div>

              <button 
                className="btn pink lighten-3" 
                style={{ marginLeft: '0.75rem'}}
                onClick={ sendHandler }
                disabled={ loading }>
                Send
              </button>

          </div>

        </div>
      </div>
    </div>
  </div>
  )
}
