import React, {useState, useEffect, useCallback} from 'react'

export const ChatWindow = ( {messageToShow} )=> {

  // const {allMessages, setAllMessages} = useState([])
  const [a, setA] = useState(1)

  const allMessages = []
  if (messageToShow.length > 1) {
    allMessages.push(...messageToShow)
  } else {

  }
  console.log('shit: ', allMessages)


  // if (!allMessages) {
  //   return (
  //     <div> No any messages yet </div> )
  // }
  // console.log('allMessages: ', allMessages)

  return (
        <div id="chat-zone">
          { allMessages.map( (message, index) => {
            var dt = message.date
            var s = dt.split('-').join(',').split('T').join(',').split(':').join(',').split('.').join(',').split(',')
            var cool = new Date(s[0], s[1]-1, s[2], s[3], s[4], s[5])
            var off = cool.getTimezoneOffset();
            // var a = cool.getMinutes()
            cool.setMinutes(cool.getMinutes() - off )
            var a = cool.toLocaleString('ru-RU', { timeZone: 'Europe/Moscow' }) // it's dont work. set default time as in browser
            // a = cool

            return (
              <>
              {/* <hr/> */}
              <span className="msg-author">
                {message.login}
              </span>
              {'\u00A0'}{'\u00A0'}
              <span className="msg-date">
                {/* {message.date} */}
                {'\u00A0'}
                { dt }
                {'\u00A0'}
                { a }
              </span>

              <span className="msg-text flow-text">
                {message.text}
              </span>
              </>
            )
          } ) }
        </div>
  )
}
