export const ChatWindow = ( {messageToShow} )=> {

  // const {allMessages, setAllMessages} = useState([])
  const [a, setA] = useState(1)

  const allMessages = []

  if (messageToShow.length > 1) {
    allMessages.push(...messageToShow)
