import React, {useState, useEffect} from 'react'
import {useHttp} from '../hooks/http.hook'
import {useMessage} from '../hooks/message.hook'

export const AuthPageLogin = () => {
  // import myself created hooks and other hooks
  const {loading, error, request, clearError} = useHttp()
  const message = useMessage()
  const [form, setForm] = useState( {email: '', login:'', password: ''} )

  useEffect( () => {
    message(error)
    clearError()
  }, [error, message, clearError])


  // refreshing form onChange
  const changeFormHandler = event => {
    setForm({...form, [event.target.name]: event.target.value })
  }


  // for login
  const loginUserHandler = async () => {
    try {
      const data = await request('/login', 'POST', {...form})
      message(data.message)
    } catch (e) {} 
  }


  return (
    <div className="row">
      <div className="col s6 offset-s3">
        <div className="card pink lighten-3">
          <div className="card-content white-text">



              <div className="input-container">

                <div className="input-field">
                  <input 
                    id="login" 
                    name="login"
                    type="text" 
                    className="auth" 
                    placeholder="Input your login"
                    onChange={changeFormHandler} 
                    />
                  <label htmlFor="login">Login</label>
                </div>

                <div className="input-field">
                  <input 
                    id="password" 
                    name="password"
                    type="password" 
                    className="auth" 
                    placeholder="Input your password"
                    onChange={changeFormHandler} />
                  <label htmlFor="password">Password</label>
                </div>
              </div>

              <button 
                className="btn blue lighten-2" 
                onClick={loginUserHandler}
                disabled={loading} >
                Login!
              </button>


          </div>
        </div>
      </div>
    </div>
  )
}
