import React, {useState} from 'react'
import {useHttp} from '../hooks/http.hook'

export const ChatCard = ({ chat, token, chatId}) => {

  console.log(chat)
  console.log(token)

  const [newMessage, setNewMessage] = useState(null)
  const {request, loading} = useHttp()

  const changeHandler = event => {
    setNewMessage(event.target.value)
  }

  // make request with newMessage
  const sendHandler = async () => {
    try {
      const fetched = await request (
        `/api/chat/chatrooms/${chatId}/send_new_messagesss`,
        'POST',
        {text: newMessage},
        {Authorization: `Bearer ${token}`}
      )
      // ???????????
      console.log(fetched) 
    } catch (e) {}
  }



  return (
  <div style={{marginTop: '2rem', width: '100%'}} >

    <link href="https://fonts.googleapis.com/icon?family=Material+Icons" rel="stylesheet"></link>
    <a className="waves-effect waves-light btn"><i className="material-icons right"> near_me </i> Chat </a>
    <a className="waves-effect waves-light btn"><i className="material-icons right">insert_emoticon</i> Members </a>
    <a className="waves-effect waves-light btn"><i className="material-icons right">live_tv</i>Video Call</a>

    <div className="row">
      <div className="col" style={{marginLeft: '-0.75rem', width:'100%'}}>
        <div className="card blue lighten-5">

          <div className="card-content black-text" >
            <span className="card-title">Card Title</span>
            LOGIC
          </div>

          <div className="card-action">

              <div className="input-field col s12">
                <textarea 
                  id="textarea" 
                  className="materialize-textarea"
                  onChange={changeHandler} />
                <label htmlFor="textarea">Your Message</label>
              </div>

              <button 
                className="btn pink lighten-3" 
                style={{ marginLeft: '0.75rem'}}
                onClick={ sendHandler }
                disabled={ loading }>
                Send
              </button>

          </div>

        </div>
      </div>
    </div>
  </div>
  )
}
