import React, {useState, useEffect} from 'react'
import {useHttp} from '../hooks/http.hook'
import {useMessage} from '../hooks/message.hook'

// &&&&&?????
import {AuthPageLogin} from './AuthPage-Login'
// import {AuthPageRegister} from './AuthPage-Register'

import {Switch, Route, BrowserRouter, Link} from 'react-router-dom'

export const AuthPage = () => {
  // import myself created hooks and other hooks
  const {loading, error, request, clearError} = useHttp()
  const message = useMessage()
  const [form, setForm] = useState( {email: '', login:'', password: ''} )

  useEffect( () => {
    message(error)
    clearError()
  }, [error, message, clearError])


  // refreshing form onChange
  const changeFormHandler = event => {
    setForm({...form, [event.target.name]: event.target.value })
  }

  // make request to backend from useHttpHook with form fields. Get back data
  const reqisterUserHandler = async () => {
    try {
      const data = await request('/register', 'POST', {...form})
      message(data.message)
    } catch (e) {} // errors already processed in useHttp hook
  }

  // for login
  const loginUserHandler = async () => {
    try {
      const data = await request('/login', 'POST', {...form})
      message(data.message)
    } catch (e) {} 
  }

  // Component for Registraton Form
  function AuthPageRegister() {
  return (
    <div className="input-container">

        <div className="input-field">
          <input 
            id="login" 
            name="login"
            type="text" 
            className="auth" 
            onChange={changeFormHandler} 
            />
          <label htmlFor="login">Login</label>
        </div>

        <div className="input-field">



          <input 
            id="password" 
            name="password"
            type="password" 
            className="auth" 
            onChange={changeFormHandler} />
          <label htmlFor="password">Password</label>



        </div>

        <button 
          className="btn blue lighten-2" 
          onClick={loginUserHandler}
          disabled={loading} >
          Login!
        </button>

    </div>
    )}

  return (
    <BrowserRouter>
    <div className="row">
      <div className="col s6 offset-s3">
        <div className="card pink lighten-3" id='auth'>
          <div className="card-content white-text">
            <span className="card-title">Authorizathion</span>
            <p id='p-up'>Only authenticated users can create and connect to chatrooms.</p>


          {/* <div className="input-container">
              <div className="input-field">
                <input 
                  id="email" 
                  name="email"
                  type="text" 
                  className="auth" 
                  placeholder="Input your email"
                  onChange={changeFormHandler} 
                  />
                <label htmlFor="email">E-mail</label>
              </div>

            <div className="input-field">
              <input 
                id="login" 
                name="login"
                type="text" 
                className="auth" 
                placeholder="Input your login"
                onChange={changeFormHandler} 
                />
              <label htmlFor="login">Login</label>
            </div>

            <div className="input-field">
              <input 
                id="password" 
                name="password"
                type="password" 
                className="auth" 
                placeholder="Input your password"
                onChange={changeFormHandler} />
              <label htmlFor="password">Password</label>
            </div>
          </div>

          <button 
            className="btn blue lighten-2" 
            onClick={reqisterUserHandler}
            disabled={loading} >  {/* disble if still loading data from backend

          </button> */}





          {/* <div className="card-action">
            <Link to='/show-login'>
              <button className="btn blue lighten-2">Log In</button>
            </Link>

            <Link to='/show-register'>
              <button className="btn grey lighten-2 black-text" id="sign-up">Sign Up</button>
            </Link>
          </div> */}

          <Link to='/show-register'>Sign Up</Link>
          {'\u00A0'}{'\u00A0'}{'\u00A0'}
          <Link to='/show-login'>Log In</Link>




          <Switch>
              <Route path='/show-register' component={AuthPageRegister} />
              <Route path='/show-login' component={AuthPageLogin} />
          </Switch>

          </div>
        </div>
      </div>
    </div> 
    </BrowserRouter>
  )
}
